import React, { useState, useEffect } from 'react';
import { AirportSearch } from './AirportSearch';
import { FlightResults } from './FlightResults';

export function SearchPage() {
  const [departureAirport, setDepartureAirport] = useState('');
  const [arrivalAirport, setArrivalAirport] = useState('');
  const [searchResults, setSearchResults] = useState(null);
  const [showResults, setShowResults] = useState(false);

  // Load initial values from URL params
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const dep = params.get('from');
    const arr = params.get('to');

    if (dep) setDepartureAirport(dep);
    if (arr) setArrivalAirport(arr);
  }, []);

  const handleSearch = () => {
    if (departureAirport && arrivalAirport) {
      // Update URL with search parameters
      const params = new URLSearchParams();
      params.set('from', departureAirport);
      params.set('to', arrivalAirport);

      window.history.pushState(
        {}, 
        '', 
        `${window.location.pathname}?${params.toString()}`
      );

      // Set search results
      setSearchResults({
        departure: departureAirport,
        arrival: arrivalAirport
      });
      setShowResults(true);
    }
  };

  return (
    <div className="search-container">
      <div className="search-boxes">
        <AirportSearch 
          label="Departure"
          value={departureAirport}
          onChange={setDepartureAirport}
        />
        <AirportSearch 
          label="Arrival"
          value={arrivalAirport}
          onChange={setArrivalAirport}
        />
        <button onClick={handleSearch}>Search Flights</button>
      </div>
      <FlightResults 
        results={searchResults} 
        isVisible={showResults}
      />
    </div>
  );
}
