import React, { useState, useEffect } from 'react';
import { AirportSearch } from './AirportSearch';
import { FlightResults } from './FlightResults';
import UA_miles from '../data/UA_miles.json';
import SQ_miles from '../data/SQ_miles.json';

export function SearchPage() {
  const [departureAirport, setDepartureAirport] = useState('');
  const [arrivalAirport, setArrivalAirport] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showResults, setShowResults] = useState(false);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const dep = params.get('from');
    const arr = params.get('to');

    if (dep) setDepartureAirport(dep);
    if (arr) setArrivalAirport(arr);
  }, []);

  const findPossibleRoutes = (origin, destination, visited = new Set(), path = []) => {
    const allRoutes = [...UA_miles, ...SQ_miles];
    const routes = [];

    // Base case: if we've found a path to the destination
    if (path.length > 0 && path[path.length - 1].Arrival_IATA === destination) {
      return [path];
    }

    // Don't allow paths longer than 3 segments
    if (path.length >= 3) {
      return [];
    }

    // Get all possible next segments
    const currentPoint = path.length === 0 ? origin : path[path.length - 1].Arrival_IATA;

    // Debug log
    console.log('Searching for segments from:', currentPoint);

    const possibleSegments = allRoutes.filter(route => 
      route["Departure IATA"] === currentPoint && 
      !visited.has(route["Arrival IATA"])
    );

    // Debug log
    console.log('Found possible segments:', possibleSegments);

    // Try each possible next segment
    for (const segment of possibleSegments) {
      const newVisited = new Set(visited);
      newVisited.add(segment["Arrival IATA"]);

      const newPath = [...path, {
        Departure_IATA: segment["Departure IATA"],
        Arrival_IATA: segment["Arrival IATA"],
        Distance: segment["Flight Distance (miles)"]
      }];

      const newRoutes = findPossibleRoutes(
        origin,
        destination,
        newVisited,
        newPath
      );

      routes.push(...newRoutes);
    }

    return routes;
  };

  const handleSearch = () => {
    if (departureAirport && arrivalAirport) {
      // Update URL with search parameters
      const params = new URLSearchParams();
      params.set('from', departureAirport);
      params.set('to', arrivalAirport);

      window.history.pushState(
        {}, 
        '', 
        `${window.location.pathname}?${params.toString()}`
      );

      // Find all possible routes
      const visited = new Set([departureAirport]);
      const routes = findPossibleRoutes(departureAirport, arrivalAirport, visited);

      // Debug log
      console.log('Found routes:', routes);

      setSearchResults(routes);
      setShowResults(true);
    }
  };

  return (
    <div className="search-container">
      <div className="search-boxes">
        <AirportSearch 
          label="Departure"
          value={departureAirport}
          onChange={setDepartureAirport}
        />
        <AirportSearch 
          label="Arrival"
          value={arrivalAirport}
          onChange={setArrivalAirport}
        />
        <button onClick={handleSearch}>Search Flights</button>
      </div>
      <FlightResults 
        results={searchResults} 
        isVisible={showResults}
      />
    </div>
  );
}
