import React from 'react';
import './FlightResults.css';

export function FlightResults({ results, isVisible }) {
  if (!isVisible || !results || results.length === 0) return null;

  return (
    <div className="results-container">
      <table className="flight-results">
        <thead>
          <tr>
            <th>Departure</th>
            <th>Arrival</th>
            <th># of Stops</th>
            <th>Layovers</th>
            <th>Total Distance</th>
            <th>Economy</th>
            <th>Premium</th>
            <th>Business</th>
            <th>First</th>
          </tr>
        </thead>
        <tbody>
          {results.map((itinerary, index) => {
            const numStops = itinerary.length - 1;
            const layovers = itinerary.slice(1, -1)
              .map(segment => segment.Departure_IATA)
              .join(' → ');

            const totalDistance = itinerary.reduce((sum, segment) => sum + segment.Distance, 0);

            return (
              <tr key={index}>
                <td>{itinerary[0].Departure_IATA}</td>
                <td>{itinerary[itinerary.length - 1].Arrival_IATA}</td>
                <td>{numStops}</td>
                <td>{layovers || '-'}</td>
                <td>{totalDistance} km</td>
                <td>-</td>
                <td>-</td>
                <td>-</td>
                <td>-</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
